# Hourly_VLR.py - autogenerated file
import os
from dotenv import load_dotenv
import pandas as pd
from sqlalchemy import create_engine
import psycopg2

# Load environment variables
load_dotenv(override=True)

DB_PARAMS = {
    "dbname": os.getenv("DB_NAME"),
    "user": os.getenv("DB_USER"),
    "password": os.getenv("DB_PASSWORD"),
    "host": os.getenv("DB_HOST"),
    "port": os.getenv("DB_PORT")
}

# SQLAlchemy engine for read operations
engine_str = f"postgresql://{DB_PARAMS['user']}:{DB_PARAMS['password']}@{DB_PARAMS['host']}:{DB_PARAMS['port']}/{DB_PARAMS['dbname']}"
engine = create_engine(engine_str)

# Step 1: Fetch Utilized Cost and DA LMPS Data
query = """
    SELECT uc.hierarchy_id, uc.date, uc.he, 
           uc.data AS utilized_cost, da_lmps.data AS da_lmps
    FROM pjm_vlr_utilized_cost uc
    JOIN pjm_vlr_da_lmps da_lmps
    ON uc.hierarchy_id = da_lmps.hierarchy_id 
    AND uc.date = da_lmps.date 
    AND uc.he = da_lmps.he
    WHERE uc.hierarchy_id BETWEEN 1 AND 19
"""
df = pd.read_sql(query, engine)

# Step 2: Add date/time metadata
df["date"] = pd.to_datetime(df["date"])
df["year"] = df["date"].dt.year
df["month"] = df["date"].dt.month
df["day"] = df["date"].apply(lambda x: x.isoweekday() % 7 + 1)
df["day_type"] = df["date"].dt.dayofweek.apply(lambda x: "WeekEnd" if x in [5, 6] else "WeekDay")
# df["hour_type"] = df["he"].apply(lambda x: "OnPeak" if 7 <= x <= 22 else "OffPeak")
df["hour_type"] = df["he"].apply(lambda x: "OnPeak" if 8 <= x <= 23 else "OffPeak")
# Step 3: Define block type
def calculate_block_type(row):
    if row["day_type"] == "WeekDay" and row["hour_type"] == "OnPeak":
        return "5x16"
    elif row["day_type"] == "WeekEnd" and row["hour_type"] == "OnPeak":
        return "2x16"
    else:
        return "7x8"

df["block_type"] = df.apply(calculate_block_type, axis=1)

# Step 4: Calculate hourly cost percentage
df["data"] = df.apply(lambda row: (row["utilized_cost"] / row["da_lmps"]) 
                      if row["da_lmps"] != 0 else 0, axis=1).round(6)

# Step 5: Final columns
df = df[[
    "hierarchy_id", "date", "year", "month", "day",
    "day_type", "he", "hour_type", "block_type", "data"
]]


# Step 6: Insert into pjm_vlr_hourly_cost
try:
    conn = psycopg2.connect(**DB_PARAMS)
    cursor = conn.cursor()

    insert_query = """
        INSERT INTO pjm_vlr_hourly_cost 
        (hierarchy_id, date, year, month, day, day_type, he, hour_type, block_type, data)
        VALUES (%(hierarchy_id)s, %(date)s, %(year)s, %(month)s, %(day)s, %(day_type)s, %(he)s, %(hour_type)s, %(block_type)s, %(data)s)
        ON CONFLICT (hierarchy_id, date, he) 
        DO UPDATE SET data = EXCLUDED.data
    """

    cursor.executemany(insert_query, df.to_dict(orient="records"))
    conn.commit()
    cursor.close()
    conn.close()

    print("✅ Data inserted into `pjm_vlr_hourly_cost` successfully!")

except Exception as e:
    print(f"❌ Error inserting into `pjm_vlr_hourly_cost`: {e}")